mapper.select:
SELECT M_ID
 FROM CUSTOMER_INFO
WHERE M_EMAIL = #{email} AND C_NAME = #{name}

SELECT COUNT(*)
FROM CUSTOMER_INFO
WHERE C_NAME = #{name}
AND M_ID = #{userId}
AND M_EMAIL = #{email}

mapper.update:
UPDATE M
SET M_PW = #{newPassword}
WHERE M_ID = #{userId}

mapper.select:
SELECT M_PW
FROM "CJCS"."CUSTOMER_INFO"
WHERE M_ID = #{m_id}

SELECT * 
FROM "CJCS"."CUSTOMER_INFO"
WHERE M_ID = #{m_id}

mapper.update:
UPDATE M
m_pw = #{m_pw}, m_addr = #{m_addr}, m_phone = #{m_phone}, m_email = #{m_email},
WHERE M_ID=#{m_id}

UPDATE S
s_storename = #{s_storename}, s_storedesc = #{s_storedesc}, s_crn = #{s_crn}, s_deliveryfee = #{s_deliveryfee}, s_latefee = #{s_latefee}
WHERE S_ID=#{m_id}

mapper.select:
SELECT *
FROM (SELECT rownum AS rnum, m_id, s_storename
      FROM (SELECT m_id, s_storename
            FROM FAVSTORE, M, S
            WHERE favs_s_id = s_id AND s_id = m_id AND favs_c_id = #{c_id}
            ORDER BY favs_date DESC
           )
     )
WHERE rnum BETWEEN #{start} AND #{end}

SELECT *
FROM (SELECT rownum AS rnum, b_s_id, b_isbn, b_title, b_author
      FROM (SELECT b_s_id, b_isbn, b_title, b_author
            FROM FAVBOOK, B
            WHERE favb_s_id = b_s_id AND favb_b_isbn = b_isbn AND favb_c_id = #{c_id}
            ORDER BY favb_date DESC
           )
     )
WHERE rnum BETWEEN #{start} AND #{end}
